mapper.update:
UPDATE TB_FREEBOARD
SET FREEBOARD_TITLE = #{freeboardTitle},
    FREEBOARD_CONTENT = #{freeboardContent},
    UPDATED_AT = SYSTIMESTAMP,
    UPDATED_BY = #{userId}
WHERE FREEBOARD_ID = #{freeboardId}

mapper.select:
SELECT COUNT(*)
FROM TB_FREEBOARD
WHERE FREEBOARD_ID = #{freeboardId}
  AND USER_ID = #{userId}

SELECT COUNT(*)
FROM TB_FREEBOARD_COMMENT
WHERE FREEBOARD_COMMENT_ID = #{commentId}
  AND USER_ID = #{userId}
